ce(/[*_\[\]`]/g, '');   }    const videos = searchData.map(video => {     const stats = statsData.find(stat => stat.id === video.id.videoId);     return {       ...video,       statistics: stats ? stats.statistics : {}     };   });    let message = `üî• Trending Videos untuk: ${cleanText(keyword)}
